import L from "leaflet";
import EventBus from "../utils/EventBus.js";
export default {
  name: "bottomMap",
  props: {
    propsConfig: {
      type: Object
    },
    indicatorsShow: {
      type: Boolean,
      required: false
    },
    gridType: {
      type: String,
      required: true
    },
    gridsAlert: {
      type: Array,
      required: true
    },
    secondLayerGeo: {
