  components: {},
  data: function data() {
    return {
      goodColor: "#2a7804",
      notGoodColor: "#047878",
      generalColor: "#f7d307",
      riskColor: "#f7a307",
      alertColor: "#f71b07",
      baseUrl: process.env.BASE_URL,
      map: null,
      geojsonLayer: null,
      gridsNum: [],
      grids: "",
      streetName: [],
      criticalValue1: "",
      // 临界值1
      criticalValue2: "",
      // 临界值2
      criticalValue3: "",
      // 临界值3
      alertJudge: "",
      // 阈值判断条件
      explainJudge: "",
      // 临界值判断条件
      indexValue: "",
      // 当前的值
