      // 当前街道的值
      alertStreet: "",
      // 预警的街道
      alertLayers: []
    };
  },
  computed: {
    showBottom: function showBottom() {
      if (this.indicatorsShow) {
        return "36%";
      } else {
        return "10%";
      }
    },
    showRight: function showRight() {
      if (this.indicatorsShow) {
        return "24%";
      } else {
        return "10%";
      }
    }
  },
  // 回到地图中心
  watch: {
    gridType: function gridType() {
      this.addGridLayer();
    },
    propsConfig: function propsConfig() {
      this.criticalValue1 = this.propsConfig.criticalValue1;
      this.criticalValue2 = this.propsConfig.criticalValue2;
      this.criticalValue3 = this.propsConfig.criticalValue3;
      this.alertJudge = this.propsConfig.alertJudge;
      this.alertValue = this.propsConfig.alertValue;
      this.explainJudge = this.propsConfig.explainJudge;
      this.indexValue = this.propsConfig.indexValue;
      this.streetsData = this.propsConfig.streetsData;
      var alertStreet = [];

      for (var key in this.streetsData) {
        if (this.alertValue < this.streetsData[key] && this.alertJudge === "high") {
          if (typeof this.$store.state.allStreetNameCode[key] != "undefined") {
            alertStreet.push(this.$store.state.allStreetNameCode[key]);
          }
        } else if (this.alertValue > this.streetsData[key] && this.alertJudge === "low") {
          if (typeof this.$store.state.allStreetNameCode[key] != "undefined") {
            alertStreet.push(this.$store.state.allStreetNameCode[key]);
          }
        }
      }

      this.alertStreet = alertStreet;
      this.addGridLayer();
    }
  },
  beforeMount: function beforeMount() {
    EventBus.$on("clearLayerStyle", this.clearStyle);
  },
  beforeDestroy: function beforeDestroy() {// EventBus.$off("clearLayerStyle", this.clearStyle);
  },
  mounted: function mounted() {
    this.initMap();
  },
  methods: {
    goMapCenter: function goMapCenter() {
      if (this.gridType === "community") {
        this.map.setView([39.918399, 116.408584], 11);
      } else {
        this.map.setView([39.918399, 116.408584], 11);
      }
    },
    initMap: function initMap() {
      this.map = L.map("map", {
        zoomControl: false,
        attributionControl: false
      });
      this.$store.state.bottomMap = this.map;
      this.addGridLayer();
      this.addMapInfo(this.map); // 添加底图

      new L.TileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
        attribution: '&copy; <a href=" ">OpenStreetMap</a > contributors'
      }).addTo(this.map); // new L.TileLayer(mapboxUrl, {
      //   attribution: "dist"
      // }).addTo(this.map);
    },
    addGridLayer: function addGridLayer() {
      var _this = this;

      var mapUrl = "";

      switch (this.gridType) {
        case "street":
          // mapUrl = `${this.baseUrl}/static/streets.json`;
          mapUrl = "".concat(this.baseUrl, "/static/spatialData/dc_jd.json");
          this.setViewGrid();
          break;

        case "community":
          // mapUrl = `${this.baseUrl}/static/grid.json`;
          mapUrl = "".concat(this.baseUrl, "/static/spatialData/dc_sq.json");
          this.setViewGrid();
          break;

        case "grid":
          // mapUrl = `${this.baseUrl}/static/grid.json`;
          mapUrl = "".concat(this.baseUrl, "/static/spatialData/dc_wg.json");
          this.setViewGrid();
          break;

        default:
          // mapUrl = `${this.baseUrl}/static/grid.json`;
          mapUrl = "".concat(this.baseUrl, "/static/spatialData/dc_wg.json");
          this.setViewGrid();
          break;
      }

      this.$http.get(mapUrl).then(function (result) {
        if (result.data && _this.geojsonLayer != null) {
          _this.map.removeLayer(_this.geojsonLayer);
        }

        var n = 1;
        var streetNmae = [];
        var allStreetName = [];
        var allStreetNameCode = [];
        var streetCode = [];
        var streetNameCode = {}; // 街道个数

        _this.$store.state.streetNumber = result.data.features.length; // FIXME: 处理街道名称保存到vuex中为二维数组

        result.data.features.forEach(function (element) {
          allStreetName.push(element.properties.JDNAME);
          allStreetNameCode.push(element.properties.JDCODE);

          if (_this.propsConfig.streetsData !== "") {
            if (typeof _this.propsConfig.streetsData !== "undefined") {
              element.properties.gridValue = String(_this.propsConfig.streetsData[element.properties.JDCODE]);
            }
          }

          streetNmae.push(element.properties.JDNAME);
          streetCode.push(element.properties.JDCODE);

          for (var i = 0; i < streetNmae.length; i++) {
            streetNameCode[streetCode[i]] = streetNmae[i];
          } // 将街道号和对应的名字对象存入vuex


          _this.$store.state.streetNameCode = streetNameCode;
          element.properties.girdIndex = n++;

          _this.streetName.push(element.properties.JDNAME);
        });

        var allStreetValue = _this.transformObj(allStreetNameCode, allStreetName);

        _this.$store.state.allStreetNameCode = allStreetValue;
        _this.$store.state.streetName = _this.streetName.map(function (key, value) {
          return [key, value];
        });
        _this.$store.state.streestName = _this.streetName.map(function (key, value) {
          return [key, value];
        });
        _this.geojsonLayer = L.geoJSON(result.data, {
          style: _this.style,
          onEachFeature: _this.onEachFeature
        });

        _this.geojsonLayer.addTo(_this.map);
      });
    },
    // eslint-disable-next-line
    style: function style(feature) {
      this.gridsNum = this.gridsAlert.map(function (item) {
        return item[0].replace(/[^0-9]/gi, "");
      }); //  处理单击网格预警文字单击事件

      if (this.$store.state.GridsAlertText == feature.properties.JDNAME) {
        return {
          opacity: 1,
          color: "#0096A1",
          fillColor: "#0096A1",
          weight: 2,
          fillOpacity: 0.8
        };
      } // 柱状图点击


      if (this.$store.state.barInfo.barName == feature.properties.JDNAME) {
        return {
          opacity: 1,
          color: "".concat(this.$store.state.barInfo.barColor),
          fillColor: "".concat(this.$store.state.barInfo.barColor),
          weight: 2,
          fillOpacity: 0.8
        };
      }

      var gridValue = feature.properties.gridValue; // 预警

      if (this.alertJudge === "high" && gridValue > this.alertValue) {
        return {
          opacity: 1,
          color: this.alertColor,
          fillColor: this.alertColor,
          weight: 2,
          fillOpacity: 0.8
        };
      } else if (this.alertJudge === "low" && gridValue < this.alertValue) {
        return {
          opacity: 1,
          color: this.alertColor,
          fillColor: this.alertColor,
          weight: 2,
          fillOpacity: 0.8
        };
      }

      if (this.explainJudge === "forward" || this.propsConfig.explainJudge === "forward") {
        if (gridValue < this.propsConfig.criticalValue1) {
          // 低
          return {
            opacity: 1,
            color: this.riskColor,
            fillColor: this.riskColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else if (gridValue > this.propsConfig.criticalValue1 && gridValue < this.propsConfig.criticalValue2) {
          // 较低
          return {
            opacity: 1,
            color: this.generalColor,
            fillColor: this.generalColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else if (gridValue > this.propsConfig.criticalValue2 && gridValue < this.propsConfig.criticalValue3 // 较高
        ) {
            return {
              opacity: 1,
              color: this.notGoodColor,
              fillColor: this.notGoodColor,
              weight: 2,
              fillOpacity: 0.8
            };
          } else if (gridValue > this.propsConfig.criticalValue3) {
          return {
            opacity: 1,
            color: this.goodColor,
            fillColor: this.goodColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else {
          return {
            opacity: 1,
            color: "#2a7804",
            fillColor: "#2a7804",
            dashArray: 3,
            fillOpacity: 0.5
          };
        }
      } else if ( // 负向评价
      this.explainJudge === "negative" || this.propsConfig.explainJudge === "negative") {
        if (gridValue < this.propsConfig.criticalValue1) {
          // 高
          return {
            opacity: 1,
            color: this.goodColor,
            fillColor: this.goodColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else if ( // 较高
        gridValue > this.propsConfig.criticalValue1 && gridValue < this.propsConfig.criticalValue2) {
          return {
            opacity: 1,
            color: this.notGoodColor,
            fillColor: this.notGoodColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else if ( // 较低
        gridValue > this.propsConfig.criticalValue2 && gridValue < this.propsConfig.criticalValue3) {
          return {
            opacity: 1,
            color: this.generalColor,
            fillColor: this.generalColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else if (gridValue > this.propsConfig.criticalValue3) {
          // 低
          return {
            opacity: 1,
            color: this.riskColor,
            fillColor: this.riskColor,
            weight: 2,
            fillOpacity: 0.8
          };
        } else {
          return {
            opacity: 1,
            color: "#FFEBD6",
            fillColor: "#FFEBD6",
            dashArray: 3,
            fillOpacity: 0.5
          };
        }
      }
    },
    // 两个数组转换为对象
    transformObj: function transformObj(list, values) {
      var result = {};

      for (var i = 0; i < list.length; i++) {
        if (values) {
          result[list[i]] = values[i];
        } else {
          result[list[i][0]] = list[i][1];
        }
      }

      return result;
    },
    // 加入图列显示
    addMapInfo: function addMapInfo(map) {
      var info = L.control();

      info.onAdd = function () {
        this._div = L.DomUtil.create("div", "info"); // create a div with a class "info"

        this.update();
        return this._div;
      };
      /* eslint-disable */


      info.update = function (props) {
        this._div.innerHTML = "";
      };

      info.addTo(map);
    },
    mouseoutClear: function mouseoutClear() {
      var _this2 = this;

      this.geojsonLayer.getLayers().forEach(function (val) {
        _this2.geojsonLayer.resetStyle(val);
      });
      this.map.closePopup();
    },
    // 清除地图的样式
    clearStyle: function clearStyle() {
      var _this3 = this;

      this.geojsonLayer.getLayers().forEach(function (val) {
        _this3.geojsonLayer.resetStyle(val);
      });

      if (this.alertLayers.length > 0) {
        for (var i = 0; i < this.alertLayers.length; i++) {
          this.map.removeLayer(this.alertLayers[i]);
        }
      }

      this.map.closePopup();
    },
    clearSecondStyle: function clearSecondStyle() {
      var _this4 = this;

      this.secondGeoLayer.getLayers().forEach(function (val) {
        _this4.secondGeoLayer.resetStyle(val);
      });
      this.map.closePopup();
    },
    onEachSecondFeature: function onEachSecondFeature(feature, layer) {
      layer.on({
        mouseover: this.showGridDesc,
        mouseout: this.clearSecondStyle
      });
    },
    // 重新设置当前layer的样式
    resetStyle: function resetStyle(e) {
      if (this.geojsonLayer) {
        this.geojsonLayer.resetStyle(e.target);
        this.map.closePopup();
      }
    },
    onEachFeature: function onEachFeature(feature, layer) {
      layer.on({
        mouseover: this.showGridDesc,
        mouseout: this.mouseoutClear,
        click: this.showGridIndexDetail
      }); // let a = layer.getBounds().getCenter();
      // console.log(a);
      // 处理预警街道,添加marker FIXME:
      // if (this.alertStreet.length > 0) {
      //   this.alertStreet.forEach(item => {
      //     if (item === layer.feature.properties.JDNAME) {
      //       // geojson 坐标数组
      //       let alertlat = layer.getBounds().getCenter();
      //       let alertlatLang = [alertlat.lat, alertlat.lng];
      //       // 添加预警
      //       let myIcon = L.divIcon({
      //         html:
      //           "<div class='red_point_alert'><div class='dot'></div><div class='pulse'></div></div>"
      //         // className: "red_point_alert"
      //       });
      //       let marker = L.marker(alertlatLang, { icon: myIcon });
      //       this.alertLayers.push(marker);
      //       marker.addTo(this.map);
      //     }
      //   });
      // }
    },
    // 鼠标移动事件
    showGridDesc: function showGridDesc(e) {
      var layer = e.target;
      layer.setStyle({
        weight: 3,
        color: "#012134",
        opacity: 0.5,
        dashArray: 3,
        fillColor: "".concat(e.target.options.color),
        fillOpacity: 1
      });
      var latlng = layer.getCenter();
      L.popup({
        closeButton: false
      }).setLatLng(latlng).setContent("".concat(layer.feature.properties["JDNAME"], " ").concat(layer.feature.properties["gridValue"], " ")).openOn(this.map);
    },
    // 点击事件
    showGridIndexDetail: function showGridIndexDetail(e) {
      var layer = e.target;
      this.geojsonLayer.resetStyle(this.geojsonLayer.getLayers()[0]);
      layer.setStyle({
        weight: 3,
        opacity: 0.5,
        dashArray: 3,
        fillColor: "".concat(e.target.options.fillColor),
        fillOpacity: 0.5
      });
      var props = layer.feature.properties;
      var id = "";
      var name = "";

      switch (this.gridType) {
        case "street":
          id = props["JDCODE"];
          name = props["JDNAME"];
          break;

        case "community":
          id = props["JDCODE"];
          name = props["JDNAME"] || props["unit_name"];
          break;

        case "grid":
          id = props["JDCODE"];
          name = props["JDNAME"] || props["unit_name"];
          break;

        default:
          id = props["qucode"];
          name = props["JDNAME"] || props["unit_name"];
          break;
      }

      EventBus.$emit("showGridDetail", {
        gridId: id,
        gridName: name
      }); // });
    },
    // 设置地图的中心
    setViewGrid: function setViewGrid() {
      this.map.setView([39.891909, 116.420366], 12);
    },
    // 叠加业务数据层
    addSecondLayer: function addSecondLayer() {
      var secondGeoLayer = L.geoJSON(this.secondLayerGeo, {
        style: this.secondLayerStyle,
        onEachFeature: this.onEachSecondFeature
      });
      this.secondGeoLayer = secondGeoLayer;
      secondGeoLayer.addTo(this.map);
    },
    secondLayerStyle: function secondLayerStyle(feature) {
      if (feature.properties.gridValue > this.criticalValue2) {
        return {
          color: "#F81628",
          fillColor: "#F81628",
          weight: 2,
          fillOpacity: 1
        };
      } else if (feature.properties.gridValue > this.criticalValue2) {
        return {
          opacity: 1,
          color: "#FEFA50",
          fillColor: "#FEFA50",
          weight: 2,
          fillOpacity: 1
        };
      } else if (feature.properties.gridValue > this.criticalValue1) {
        return {
          opacity: 1,
          fillColor: "#1635F6",
          weight: 2,
          fillOpacity: 1
        };
      }
    },
    // 返回随机数
    rn: function rn(min, max) {
      var n = parseInt(Math.random() * (max - min) + min);
      return n;
    }
  }
};
