.basisGrid {
  .ivu-input {
    background: rgba(16, 41, 67, 0.75);
    border: 1px solid rgba(147, 147, 147, 0.75);
  }
  .slider {
    .ivu-slider {
      .ivu-slider-wrap {
        width: 100%;
        height: 4px;
        margin: 16px 0;
        background-color: #e2f0ff;
        border-radius: 3px;
        vertical-align: middle;
        position: relative;
        cursor: pointer;
      }
      // 拖动覆盖进度条
      .ivu-slider-bar {
        height: 4px;
        background: linear-gradient(to right, #82ace8, #55c0ee);
        border-radius: 3px;
        position: absolute;
      }
      // 拖动按钮
      .ivu-slider-button-wrap {
        width: 18px;
        height: 18px;
        text-align: center;
        background-color: transparent;
        position: absolute;
        top: -5px;
        transform: translateX(-50%);
        .ivu-slider-button {
          width: 12px;
          height: 12px;
          border: 4px solid #83ade9;
          border-radius: 50%;
          background-color: #fff;
          transition: all 0.2s linear;
          outline: 0;
        }
      }
    }
  }
}
